// Registration form JavaScript
$this->registerJs('
$(document).ready(function() {
    // Initialize Select2
    function initSelect2() {
        if (typeof $.fn.select2 !== "undefined") {
            $("#country-select").each(function() {
                if (!$(this).hasClass("select2-hidden-accessible")) {
                    try {
                        $(this).select2();
                    } catch (e) {
                        console.error("Select2 initialization error:", e);
                    }
                }
            });
        } else {
            setTimeout(initSelect2, 100);
        }
    }
    
    initSelect2();
    setTimeout(initSelect2, 500);
    
    // Password toggle functionality
    const passwordToggles = document.querySelectorAll(".kashflow-password-toggle");
    passwordToggles.forEach(toggle => {
        toggle.addEventListener("click", function() {
            const inputGroup = this.closest(".kashflow-input-group");
            const passwordInput = inputGroup.querySelector("input[type=\"password\"], input[type=\"text\"]");
            if (passwordInput.type === "password") {
                passwordInput.type = "text";
            } else {
                passwordInput.type = "password";
            }
        });
    });
    
    // Enhanced AJAX form handling
    const registrationForm = document.getElementById("registration-form");
    if (registrationForm) {
        const inputs = registrationForm.querySelectorAll("input");
        inputs.forEach(input => {
            input.addEventListener("blur", function() {
                setTimeout(() => {
                    updateFieldVisualState(input);
                }, 200);
            });
            
            input.addEventListener("input", function() {
                clearFieldErrorState(input);
            });
        });
        
        // Handle form submission with loading state
        registrationForm.addEventListener("submit", function(e) {
            const submitBtn = registrationForm.querySelector("button[type=\"submit\"]");
            const originalText = submitBtn.textContent;
            
            submitBtn.disabled = true;
            submitBtn.textContent = "Signing Up...";
            submitBtn.classList.add("loading");
            
            setTimeout(() => {
                submitBtn.disabled = false;
                submitBtn.textContent = originalText;
                submitBtn.classList.remove("loading");
            }, 3000);
        });
    }
});

// Update field visual state based on validation
function updateFieldVisualState(field) {
    const fieldGroup = field.closest(".kashflow-field-group");
    const errorElement = fieldGroup.querySelector(".kashflow-field-error");
    
    if (errorElement && errorElement.textContent.trim()) {
        fieldGroup.classList.remove("has-success");
        fieldGroup.classList.add("has-error");
    } else if (field.value.trim()) {
        fieldGroup.classList.remove("has-error");
        fieldGroup.classList.add("has-success");
    } else {
        fieldGroup.classList.remove("has-error", "has-success");
    }
}

// Clear field error state
function clearFieldErrorState(field) {
    const fieldGroup = field.closest(".kashflow-field-group");
    fieldGroup.classList.remove("has-error", "has-success");
    
    const errorContainer = fieldGroup.querySelector(".kashflow-field-error");
    if (errorContainer) {
        errorContainer.remove();
    }
}
', \yii\web\View::POS_READY);

